    private string name;
    private string errorMessage;

    private async Task Add()
    {
        if (!String.IsNullOrEmpty(name))
        {
            Item item = new Item();
            item.Name = name;
            bool isAddSuccess = await RecordsService.AddItemToRecordAsync(item);
            if (isAddSuccess)
            {
                errorMessage = "";
                NavManager.NavigateTo("GroceryList");
            }
            else
